mapper.select:
select id, code, name, type, description, status, total_quantity, claimed_quantity, used_quantity, validity_type, valid_start_time, valid_end_time, days_after_claim, discount_percent, min_spend, max_discount_amount, discount_amount, create_time, update_time from coupons
and type = #{type}
and status = #{status}
and name like concat('%',#{name},'%')
order by id desc
